package co.mobiwise.model;

public class MenuItem {
	
	String menuItemName;
	
	String menuItemPrice;
	
	String menuItemInfo;
	
	public MenuItem() {
		// TODO Auto-generated constructor stub
	}

	public String getMenuItemName() {
		return menuItemName;
	}

	public void setMenuItemName(String menuItemName) {
		this.menuItemName = menuItemName;
	}

	public String getMenuItemPrice() {
		return menuItemPrice;
	}

	public void setMenuItemPrice(String menuItemPrice) {
		this.menuItemPrice = menuItemPrice;
	}

	public String getMenuItemInfo() {
		return menuItemInfo;
	}

	public void setMenuItemInfo(String menuItemInfo) {
		this.menuItemInfo = menuItemInfo;
	}
	
}
